var Vec={};Vec.dotPr=function(a,b){return a[0]*b[0]+a[1]*b[1];};Vec.len=function(a){return Math.sqrt(Math.pow(a[0],2)+Math.pow(a[1],2));};Vec.norm=function(a){var norm=Vec.len(a); return [a[0]/norm,a[1]/norm];};Vec.neg=function(a){return [-1*a[0],-1*a[0]];};Vec.add=function(a,b){return [a[0]+b[0],a[1]+b[1]];};Vec.scale=function(a,b){return [a[0]*b,a[1]*b];};Vec.recToPol=function(a){return [Math.atan2(a[1],a[0]),Vec.len(a)];};Vec.polToRec=function(a,b){return [b*Math.cos(a),b*Math.sin(a)];};Vec.rotate=function(a,b){var c=Math.cos(b);var d=Math.sin(b);return [c*a[0]+-1*d*a[1],d*a[0]+c*a[1]];};
